void test()
{
  long i;
  pthread_t th[50];

  memset(r, -1, sizeof(r));
  srand(getpid());
  for (i = 0; i < 25; i++) {
    pthread_create(&th[i], 0, thr, (void*)i);
    usleep(rand() % 10000);
  }
  for (i = 0; i < 25; i++) {
    pthread_create(&th[25 + i], 0, thr, (void*)i);
    if (rand() % 2)
      usleep(rand() % 10000);
  }
  usleep(rand() % 100000);
}

