static void execute_call(int call);
static int running;
static int collide;

static void* thr(void* arg)
{
  struct thread_t* th = (struct thread_t*)arg;
  for (;;) {
    while (!__atomic_load_n(&th->running, __ATOMIC_ACQUIRE))
      syscall(SYS_futex, &th->running, FUTEX_WAIT, 0, 0);
    execute_call(th->call);
    __atomic_fetch_sub(&running, 1, __ATOMIC_RELAXED);
    __atomic_store_n(&th->running, 0, __ATOMIC_RELEASE);
    syscall(SYS_futex, &th->running, FUTEX_WAKE);
  }
  return 0;
}

static void execute(int num_calls)
{
  int call, thread;
  running = 0;
  for (call = 0; call < num_calls; call++) {
    for (thread = 0; thread < sizeof(threads) / sizeof(threads[0]); thread++) {
      struct thread_t* th = &threads[thread];
      if (!th->created) {
        th->created = 1;
        pthread_attr_t attr;
        pthread_attr_init(&attr);
        pthread_attr_setstacksize(&attr, 128 << 10);
        pthread_create(&th->th, &attr, thr, th);
      }
      if (!__atomic_load_n(&th->running, __ATOMIC_ACQUIRE)) {
        th->call = call;
        __atomic_fetch_add(&running, 1, __ATOMIC_RELAXED);
        __atomic_store_n(&th->running, 1, __ATOMIC_RELEASE);
        syscall(SYS_futex, &th->running, FUTEX_WAKE);
        if (collide && call % 2)
          break;
        struct timespec ts;
        ts.tv_sec = 0;
        ts.tv_nsec = 20 * 1000 * 1000;
        syscall(SYS_futex, &th->running, FUTEX_WAIT, 1, &ts);
        if (running)
          usleep((call == num_calls - 1) ? 10000 : 1000);
        break;
      }
    }
  }
}

long r[4];
void execute_call(int call)
{
  switch (call) {
  case 0:
    NONFAILING(memcpy((void*)0x20e0f000, "/dev/binder#", 13));
    r[0] = syz_open_dev(0x20e0f000, 0, 0);
    break;
  case 1:
    r[1] = syscall(__NR_epoll_create, 0x10011);
    break;
  case 2:
    syscall(__NR_readahead, r[1], 2, 2);
    break;
  case 3:
    NONFAILING(*(uint32_t*)0x20336ff4 = 0);
    NONFAILING(*(uint64_t*)0x20336ff8 = 0);
    syscall(__NR_epoll_ctl, r[1], 1, r[0], 0x20336ff4);
    break;
  case 4:
    NONFAILING(memcpy((void*)0x20000000, "/selinux/commit_pending_bools", 30));
    r[2] = syscall(__NR_openat, 0xffffffffffffff9c, 0x20000000, 1, 0);
    break;
  case 5:
    NONFAILING(*(uint32_t*)0x20000040 = 0);
    NONFAILING(*(uint16_t*)0x20000044 = 0x40);
    NONFAILING(*(uint16_t*)0x20000046 = 7);
    NONFAILING(*(uint16_t*)0x20000048 = 5);
    NONFAILING(*(uint16_t*)0x2000004a = 2);
    NONFAILING(*(uint16_t*)0x2000004c = 0x40);
    NONFAILING(*(uint16_t*)0x2000004e = 0x8001);
    NONFAILING(*(uint16_t*)0x20000050 = 0);
    NONFAILING(*(uint16_t*)0x20000052 = 0);
    NONFAILING(*(uint16_t*)0x20000054 = 2);
    NONFAILING(*(uint32_t*)0x20000080 = 0x16);
    if (syscall(__NR_getsockopt, r[2], 0x84, 0x77, 0x20000040, 0x20000080) !=
        -1)
      NONFAILING(r[3] = *(uint32_t*)0x20000040);
    break;
  case 6:
    NONFAILING(*(uint32_t*)0x200000c0 = r[3]);
    NONFAILING(*(uint16_t*)0x200000c4 = 0);
    syscall(__NR_setsockopt, r[2], 0x84, 0x19, 0x200000c0, 8);
    break;
  case 7:
    syscall(__NR_ioctl, r[0], 0x40046208, 0);
    break;
  case 8:
    syscall(__NR_close, r[1]);
    break;
  }
}

void test()
{
  memset(r, -1, sizeof(r));
  execute(9);
  collide = 1;
  execute(9);
}

int main()
{
  syscall(__NR_mmap, 0x20000000, 0x1000000, 3, 0x32, -1, 0);
  install_segv_handler();
  char* cwd = get_current_dir_name();
  for (;;) {
    if (chdir(cwd))
      fail("failed to chdir");
    use_temporary_dir();
    int pid = do_sandbox_none(0, false);
    int status = 0;
    while (waitpid(pid, &status, __WALL) != pid) {
    }
  }
}
