static void execute_one(void);

#define WAIT_FLAGS __WALL

static void loop(void)
{
  setup_loop();
  int iter;
  for (iter = 0;; iter++) {
    reset_loop();
    int pid = fork();
    if (pid < 0)
      exit(1);
    if (pid == 0) {
      setup_test();
      execute_one();
      close_fds();
      exit(0);
    }
    int status = 0;
    uint64_t start = current_time_ms();
    for (;;) {
      if (waitpid(-1, &status, WNOHANG | WAIT_FLAGS) == pid)
        break;
      sleep_ms(1);
      if (current_time_ms() - start < 5 * 1000)
        continue;
      kill_and_wait(pid, &status);
      break;
    }
  }
}

uint64_t r[2] = {0xffffffffffffffff, 0xffffffffffffffff};

void execute_one(void)
{
  intptr_t res = 0;
  res = syscall(__NR_socket, 0xaul, 0x80003ul, -1);
  if (res != -1)
    r[0] = res;
  syscall(__NR_ioctl, r[0], 0x1000008912ul, 0x20000080ul);
  NONFAILING(memcpy((void*)0x20000040, "/dev/ptmx\000", 10));
  res = syscall(__NR_openat, 0xffffffffffffff9cul, 0x20000040ul, 0ul, 0ul);
  if (res != -1)
    r[1] = res;
  NONFAILING(*(uint32_t*)0x200000c0 = 0x11);
  inject_fault(9);
  syscall(__NR_ioctl, r[1], 0x5423ul, 0x200000c0ul);
}
